public class LeanWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	System.out.println(  );

	int num = 12345;
	int rem = 0;
	int sum = 0;
	System.out.println( "sum of the degits : " + num  );
	while ( num > 0) {
		rem = num % 10;
		sum = sum + rem;
		num = num /10;
		
	}
		System.out.println(    + sum );
	}
	
}
	
	
	
			

